(function(global){
  function createSVG(icon){
    var svgNS = "http://www.w3.org/2000/svg";
    var svg = document.createElementNS(svgNS, "svg");
    svg.setAttribute("xmlns", "http://www.w3.org/2000/svg");
    svg.setAttribute("viewBox", "0 0 24 24");
    svg.setAttribute("fill", "none");
    svg.setAttribute("stroke", "currentColor");
    svg.setAttribute("stroke-width", "2");
    svg.setAttribute("stroke-linecap", "round");
    svg.setAttribute("stroke-linejoin", "round");
    svg.setAttribute("width", "24");
    svg.setAttribute("height", "24");
    var path = document.createElementNS(svgNS, "polyline");
    if(icon === "chevron-left"){
      path.setAttribute("points", "15 18 9 12 15 6");
    } else if(icon === "chevron-right"){
      path.setAttribute("points", "9 18 15 12 9 6");
    } else {
      // fallback: simple square so layout doesn't break (shouldn't be used here)
      var rect = document.createElementNS(svgNS, "rect");
      rect.setAttribute("x","4"); rect.setAttribute("y","4");
      rect.setAttribute("width","16"); rect.setAttribute("height","16");
      svg.appendChild(rect);
      return svg;
    }
    svg.appendChild(path);
    return svg;
  }
  var api = {
    replace: function(){
      var nodes = document.querySelectorAll("[data-feather]");
      for(var i=0;i<nodes.length;i++){
        var n = nodes[i];
        var icon = n.getAttribute("data-feather");
        var svg = createSVG(icon);
        // copy class & style (common behavior of feather)
        if(n.getAttribute("class")) svg.setAttribute("class", n.getAttribute("class"));
        if(n.getAttribute("style")) svg.setAttribute("style", n.getAttribute("style"));
        n.parentNode.replaceChild(svg, n);
      }
    }
  };
  global.feather = api;
})(window);